import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './adminlistItem.scss'
// import { useNavigate } from "react-router-dom"
// import React, { useState } from 'react';



export default function Listitem(props) {
  let navigate = useNavigate();

  const [genres, setGenres] = useState('')
  const [id, setId] = useState('')
  const [images, setImages] = useState('')
  const [links, setLinks] = useState('')
  const [synopsis, setSynopsis] = useState('')
  const [title, setTitle] = useState('')

  async function changeFeatured() {
        setGenres(props.genres);
        setId(props.id)
        setImages(props.images)
        setLinks(props.setLinks)
        setSynopsis(props.synopsis)
        setTitle(props.title)
        console.log()
    // event.preventDefault()
    const response = await fetch('http://localhost:1337/api/change_featured', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            genres,
            id,
            images,
            links,
            synopsis,
            title
        }),
    })

    const data = await response.json()

    if (data.status === 'ok') {
        console.log("worked!?")
    }
}

  return (
    <div className='admin-listItem'>
      <form>
      <input type="submit" value="submit" onSubmit={changeFeatured} />
      </form>

      <div className="centered">New Featured</div>
        <img
            src={props.poster}
            alt=""
            type="submit"
        />

        <div className='itemInfo'>
        <div className='title'>
            {props.title}
          </div>
      </div>
    </div>
  )
}
