import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons'
import { useRef, useState } from 'react'
import Listitem from '../listItem/Listitem'
import './list.scss'

export default function List() {

    // probably better to use a library --> look into this later
    const[isMoved, setIsMoved] = useState(false)
    const[slideNumber, SetSlideNumber] = useState(0)
    const slider_size = 4

    const[flag, setFlag] = useState(true)

    const listRef = useRef() // react element

    const handleClick = (direction) => {
        setIsMoved(true)
        let distance  = listRef.current.getBoundingClientRect().x - 50
        if(direction === "left" && slideNumber > 0)
        {
            listRef.current.style.transform = `translateX(${325+distance}px)`
            SetSlideNumber(slideNumber-1)
        }
                            
            
        if(direction === "right" && slideNumber < slider_size)
        {
            listRef.current.style.transform = `translateX(${-325+distance}px)`
            SetSlideNumber(slideNumber+1)
        }
            
        console.log("XD")
        console.log(distance)
    }

  return (
    <div className='list'>
        <span className='listTitle'>Continue to Watch</span>
        <div className="wrapper">
            <ArrowLeftOutlined 
                className='slider left' 
                onClick={()=>handleClick("left")}
                style={{display: !isMoved && "none"}}
                disabled={flag}
            />
            <div className='container' ref={listRef}>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
                <Listitem/>
            </div>
            <ArrowRightOutlined className='slider right'onClick={()=>handleClick("right")}/>
        </div>
    </div>
  )
}
