import React from 'react'
import Helmet from 'react-helmet'
import Anicontent from '../components/anicontent/Anicontent'
import Footer from '../components/footer/Footer'
import Navbar from '../components/navbar/Navbar'
import List from "../components/list/List"
import "./anime.scss"
import Aniheader from '../components/aniheader/Aniheader'
import { useEffect } from "react"
import jwt from 'jwt-decode'
import { useNavigate } from "react-router-dom"


export default function Anime() {

  let navigate = useNavigate();
  const token = localStorage.getItem('token')
  const [action, setAction] = useState([])
  
	useEffect(() => {

    window.scrollTo(0, 0) 
    if (token) {
      const user = jwt(token)
      if (!user) {
        console.log('jkhg')
        localStorage.removeItem('token')
        navigate('/login')
      }
    }
    else
    {
      console.log('asdasd')
      localStorage.removeItem('token')
      navigate('/login')
    }
	}, [])

  return (
    <div className="anime">
        <Helmet>
            <title>{ "Ani-Fox" }</title>
        </Helmet>

        <Navbar/>
        <Aniheader aniId={localStorage.getItem('LS_ID')}/>
        <br />
        <Anicontent aniId={localStorage.getItem('LS_ID')}/>


        {/* <List title="Recommended" data={}/> */}
        <br />
        <Footer/>
       
    </div>
  )
}
