import { AddOutlined, PlayArrowOutlined, ThumbDownAltOutlined, ThumbUpOutlined } from '@material-ui/icons'
import './listitem.scss'
import { useNavigate } from "react-router-dom"
// import React, { useState } from 'react';



export default function Listitem(props) {

  async function changeFeatured() {

    console.log(props.links)
    const response = await fetch('http://localhost:1337/api/change_featured', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            genres,
            id,
            images,
            links,
            synopsis,
            title
        }),
    })

    const data = await response.json()

    if (data.status === 'ok') {
        console.log("Succesfull Change")
        window.location.reload(false);
    }
}


  let navigate = useNavigate();
  return (
    <div className='listItem'>
        <img
            onClick={() => {
              if(props.isAnime)
              {
                localStorage.setItem('LS_ID', props.aniId)
                window.location.reload()
              }
              else
              {
                localStorage.setItem('LS_ID', props.aniId)
                navigate("/anime")
              }
            }} 
            src={props.poster}
            alt="" 
        />

        <div className='itemInfo'>
        <div className='title'>
            {props.title}
          </div>
          <div className="icons">
            <PlayArrowOutlined className='icon' 
              onClick={() => {
                if(props.isAnime)
                {
                  localStorage.setItem('LS_ID', props.aniId)
                  window.location.reload()
                }
                else
                {
                  localStorage.setItem('LS_ID', props.aniId)
                  navigate("/anime")
                }
              }} 
            />
            <AddOutlined className='icon'/>
            <ThumbUpOutlined className='icon'/>
            <ThumbDownAltOutlined className='icon'/>
          </div>

      </div>
    </div>
  )
}
