import React, { useEffect, useState } from 'react'
import "./anicontent.scss"


export default function Anicontent(props) {
   
  const[select, setSelect] = useState('');

  // const [aniId, setAniId] = useState(5114)
  const [aniData, setAniData] = useState([])
  const aniId = localStorage.getItem('LS_ID')
  const [user, setUser] = useState(localStorage.getItem('user_email'))
  const [userLikes, setUserLikes] = useState([])

  async function get_user_likes() {    
    const response = await fetch('http://localhost:1337/api/get_user_likes', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            user,
        }),
    })

    const data = await response.json()

    if (data.status === 'ok') {
        console.log("User Likes: Sucess")
        // window.location.reload(false);
    }
  }

  async function fetchAniData() 
  {
      try 
      {

        const response = await fetch('http://localhost:1337/api/anime', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            aniId,
          }),
        });

        const mylist = await response.json();
        // console.log(mylist)
        return mylist;
      } 
      catch (error) 
      {
          console.log('ERROR')
          console.error(error);
      }
  }

  useEffect(() => {
    async function getAniData() 
    {  
      const aniDataList = await fetchAniData();
      setAniData(aniDataList)

      const userList = await get_user_likes()
      setUserLikes(userLikes)
    }

    getAniData()
    // eslint-disable-next-line
  }, [])

 
  try {
    return (

      <div className="anicontent">
          <h1>testetetetette</h1>
          <div className="container">
  
            <div className="right">
  
              <video 
              controls  
                src= {select}  // passing a javascript variable
              />
            </div>
  
            <div className='left'>
              <select className='themes' onChange={e=>setSelect(e.target.value)} defaultValue="none">
                <option value="none" disabled hidden>Select Episode</option>
  
                {aniData.links.map((lists, i) => {
                return (
                  <option 
                    key={i}
                    value={lists}>
                    Episode {aniData.links.indexOf(lists) + 1}
                  </option>
                  )
                })}
                
              </select>
            </div>
  
  
          </div>
      </div>
    )
  } catch (error) {
    return (

      <div className="anicontent">
        This should not load
      </div>
    ) 
  }


}
