import { AddOutlined, PlayArrowOutlined, ThumbDownAltOutlined, ThumbUpOutlined } from '@material-ui/icons'
import './listitem.scss'
import { useNavigate } from "react-router-dom"
import { useEffect, useState } from 'react';


export default function Listitem(props) {

  let navigate = useNavigate();
  const [lid, setLid] = useState('')
  const [user, setUser] = useState('')
  const [anime, setAnime] = useState('')
  const [genres, setGenres] = useState('')

  async function changeFeatured() {

    console.log(props.links)
    const response = await fetch('http://localhost:1337/api/like', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            lid,
            user,
            anime,
            genres,
        }),
    })

    const data = await response.json()

    if (data.status === 'ok') {
        console.log("Succesfull Change")
        window.location.reload(false);
    }
  }

  useEffect(() => {
    async function get_data() 
    {

      setGenres(props.genres);
      setId(props.aniId)
      setImages(props.poster)
      setLinks(props.links[0])
      setSynopsis(props.synopsis)
      setTitle(props.title)
    }
    get_data()
     // eslint-disable-next-line
  },[])
  return (
    <div className='listItem'>
        <img
            onClick={() => {
              if(props.isAnime)
              {
                localStorage.setItem('LS_ID', props.aniId)
                window.location.reload()
              }
              else
              {
                localStorage.setItem('LS_ID', props.aniId)
                navigate("/anime")
              }
            }} 
            src={props.poster}
            alt="" 
        />

        <div className='itemInfo'>
        <div className='title'>
            {props.title}
          </div>
          <div className="icons">
            <PlayArrowOutlined className='icon' 
              onClick={() => {
                if(props.isAnime)
                {
                  localStorage.setItem('LS_ID', props.aniId)
                  window.location.reload()
                }
                else
                {
                  localStorage.setItem('LS_ID', props.aniId)
                  navigate("/anime")
                }
              }} 
            />
            <AddOutlined className='icon'/>
            <ThumbUpOutlined className='icon'/>
            <ThumbDownAltOutlined className='icon'/>
          </div>

      </div>
    </div>
  )
}
