import { useEffect, useState } from 'react';
import './adminlistItem.scss'
// import { useNavigate } from "react-router-dom"

const likes = 12

export default function Listitem(props) {
  // let navigate = useNavigate();

  const [genres, setGenres] = useState('')
  const [id, setId] = useState('')
  const [images, setImages] = useState('')
  const [links, setLinks] = useState('')
  const [synopsis, setSynopsis] = useState('')
  const [title, setTitle] = useState('')
  const [likes, setLikes] = useState([])


  async function changeFeatured() {

    console.log(props.links)
    const response = await fetch('http://localhost:1337/api/change_featured', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            genres,
            id,
            images,
            links,
            synopsis,
            title
        }),
    })

    const data = await response.json()

    if (data.status === 'ok') {
        console.log("Succesfull Change")
        window.location.reload(false);
    }
}

async function get_anime_likes() {
  try {
    const response = await fetch('http://localhost:1337/api/get_anime_likes', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
      },
      body: JSON.stringify({
          id,
      }),
    });
    const mylist = await response.json();
    return mylist;
  } catch (error) {console.error(error);}  


}

useEffect(() => {
  async function get_data() 
  {

    setGenres(props.genres);
    setId(props.aniId)
    setImages(props.poster)
    setLinks(props.links[0])
    setSynopsis(props.synopsis)
    setTitle(props.title)
    setLikes(get_anime_likes()) 
  }

  get_data()
  console.log(likes)
   // eslint-disable-next-line
},[])

  return (
    <div 
      className='admin-listItem' 
      onClick={() => {
        changeFeatured()
       }} >

      <div className="newfeat"> New Featured </div>

      <div className='wrapper'>
        <div className="likes"> {likes} likes </div>

        <img
            src={props.poster}
            alt="" />
      </div>


        <div className='itemInfo'>
        <div className='title'>
            {props.title}
          </div>
      </div>
    </div>
  )
}
